#include <stdio.h>
#include <stdlib.h>
#include <omp.h>
#include <time.h>

static int mpf(long long int value)
{
    long long int n = value;

    while(n%2==0)
        n/=2;
if(n==1) return 1;
    while(n%3==0)
        n/=3;
if(n==1) return 1;
    while(n%5==0)
        n/=5;
if(n==1) return 1;
    while(n%7==0)
        n/=7;
if(n==1) return 1;
    while(n%11==0)
        n/=11;
if(n==1) return 1;
    while(n%13==0)
        n/=13;
if(n==1) return 1;
    while(n%17==0)
        n/=17;
if(n==1) return 1;
    while(n%19==0)
        n/=19;
if(n==1) return 1;
    while(n%23==0)
        n/=23;

    while(n%29==0)
        n/=29;

    while(n%31==0)
        n/=31;

    while(n%37==0)
        n/=37;

    while(n%41==0)
        n/=41;

    while(n%43==0)
        n/=43;

    while(n%47==0)
        n/=47;
    
    if(n==1)
        return 1;
    return 0;
}

void main(int argc, char **argv){
    FILE *f;
    long long int i;
    struct timespec tstart={0,0}, tend={0,0};
    
    f = fopen("list.txt","w");
    
    clock_gettime(CLOCK_MONOTONIC, &tstart);
    fprintf(f,"list={0,");
    #pragma omp parallel for private(i)
    for(i=1;i<1109496723126;i++){
        if(i%2==0){
            if(mpf(i/2)==1){
                if(mpf(i+1)==1)
                    fprintf(f,"%lld,",i);
                else
                    i++;
            }
        } else {
            if(mpf(i)==1){
                if(mpf((i+1)/2)==1)
                    fprintf(f,"%lld,",i);
                else
                    i++;
            }
        }
    }
    fprintf(f,"}\n");
    clock_gettime(CLOCK_MONOTONIC, &tend);
    
    printf("\nTiempo:%.5f\n", ((double)tend.tv_sec + 1.0e-9*tend.tv_nsec)-((double)tstart.tv_sec + 1.0e-9*tstart.tv_nsec));
    
    fclose(f);
}

